package first_selenium;

public class second_data_types {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// premitive data type size remain "fixed"
		
		//Boolean data types storage have 1 bit 
		boolean b1=true;
		boolean b2=false;
		
		// charector data type storage have 2 byte
		char c1='a';
		char c2='1';
		char c3='@';
		
		// Byte data type storage 1 byte
		byte a = 10;
		byte b = -10;
		
		// Short data type storage 2 byte
		short a1 = 10000;
		short a2 = -10000;
		
		// Interger data type storage 4 byte
		int i = 10;
		int i2 = -10;
		
		// Long data type storage 8 byte
		long d1 = 10000L;
		long d11 = -10000L;
		
		// Float data type storage 4 byte
		float f= 98.8f;
		float f1=83.32f;
		
		// Double data type storage 8 byte
		double e = 23.3;
		double e1 = 323.23;
	
				
		// non premitive data type
		// non premitive data type size varies at run time
		// not premitive data type are derived from premitive not the vise versa
		
		// String 
		String s1= "java";
		String s2="1234";
		String s3="@#@%#%";
		String s4="@#$456fhgh";
		
		// Array
		 int z[] = new int[5];

		
		
		
		
		
		
		
		
		
		
		
	}

}
